    private MovieJsonParser movieJsonParser;

    @Before
    public void setup() throws Exception {
        movieJsonParser = new MovieJsonParser(sampleMoviesJson);
    }

    @Test
    public void shouldGetTheNumberOfMoviesInAPage() throws Exception {
        assertThat(movieJsonParser.getMovies().size()).isEqualTo(20);
    }

    @Test
    public void shouldGetDetailsOfAMovie() throws Exception {
        int firstMovieIndex = 0;
        Movie firstMovie = movieJsonParser.getMovie(firstMovieIndex);

        assertThat(firstMovie.getMovieTitle()).isEqualTo(MOVIE_TITLE);
        assertThat(firstMovie.getImageResource()).isEqualTo("http://image.tmdb.org/t/p/w185/zSouWWrySXshPCT4t3UKCQGayyo.jpg");
        assertThat(firstMovie.getDescription()).isEqualTo(DESCRIPTION_FIRST_MOVIE);
        assertThat(firstMovie.getRating()).isEqualTo("6.02");
        assertThat(firstMovie.getReleaseDate()).isEqualTo("2016-05-18");

    }
